import React from 'react';
import './PriceSlider.sass';
import { Slider, Typography, Grid, TextField, InputAdornment, Button, Input} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
  root: {
    width: 500,
  },
  input: {
      width: 60,
  }
});

function valuetext(value) {
  return `$${value}`;
}

export default function RangeSlider() {
  const classes = useStyles();
  const range = [0,6000];
  const [sliderValues, setSliderValues] = React.useState(range);
  const [inputValues, setInputValue]=React.useState(range);

  const handleSliderChange = (event, newValues) => {
    setSliderValues(newValues);
    setInputValue(newValues);
  };

  const handleClearButtonClick = ()=>{
    setSliderValues(range);
    setInputValue(range);
  }

  return (
    <Grid container className={classes.root}>
        <Grid item xs={12}>
          <Typography id="range-slider" gutterBottom>
            Price range
          </Typography>
        </Grid>
      <Gird container direction="row" justify="space-between">
        <Grid item xs={12}>
          <label>Min: $</label>
          <Input
            value={inputValues[0]}
            className={classes.input}
            onChange={evt => {
              const value = evt.target.value;
              const newState = [value, inputValues[1]];
              setInputValue(newState);
              setSliderValues(newState);

            }}
          />
        </Grid>
        <Grid item xs={12}>
          <label>Max: $</label>
          <Input
            value={inputValues[1]}
            className={classes.input}
            onChange={evt => {
              const value = evt.target.value;
              const newState = [inputValues[0], value];
              setInputValue(newState);
              setSliderValues(newState);

            }}
          />
        </Grid>
      </Gird>

      <Grid container>
        <Slider
          value={sliderValues}
          onChange={handleSliderChange}
          valueLabelDisplay="auto"
          aria-labelledby="range-slider"
          getAriaValueText={valuetext}
          min={0}
          max={6000}
        />
      </Grid>







      <Button onClick={handleClearButtonClick}>Clear</Button>
    </Grid>
  );
}
