import React from 'react';
import Comment from '../Comment/Comment';
import {
    Paper
} from "@material-ui/core";
import CommentForm from '../CommentForm/CommentForm';

export default class CommentBox extends React.Component {
    constructor() {
        super();

        this.state = {
            comments: [{
                id: 1,
                author: "Fiona",
                content: "A review is an evaluation of a publication, service, or company such as a movie, video game, musical composition, book; a piece of hardware like a car, home appliance, or computer; or an event or performance, such as a live music concert, play, musical theater show, dance show, or art exhibition.",
            }, {
                id: 2,
                author: "Dan",
                content: "A review is an evaluation of a publication, service, or company such as a movie, video game, musical composition, book; a piece of hardware like a car, home appliance, or computer; or an event or performance, such as a live music concert, play, musical theater show, dance show, or art exhibition.",
            }]
        };
    }
    _getComments() {
        return this.state.comments.map((comment) => {
            return <Comment id = {
                comment.id
            }
            author = {
                comment.author
            }
            content = {
                comment.content
            }
            />
        })
    }

    _addComment(author, content) {
        const comment = {
            id: this.state.comments.length + 1,
            author: this.author,
            content: this.content
        };
        this.setState(this.state.comments.push(comment));
    }

    render() {
        const commentListStyle = {
            div: {
                padding: "14px",
            },
            paper: {
                padding: "40px 20px"
            },
        };
        return ( <
            div style = {
                commentListStyle.div
            }
            className = "App" >
            <
            h1 > Comments < /h1> <
            CommentForm / >
            <
            Paper style = {
                commentListStyle.paper
            } > {
                this._getComments
            } <
            /Paper> <
            /div>
        );
    }
}
