  content: {
        textAlign: "left",
      },
      time: {
        color: "grey",
        textAlign: "left",
      },
      divider: {
        margin: "20px",
      }
});

const Comment = ({ author, content }) => (
  <div>
    <Grid container wrap="nowrap" spacing={2}>
      <Grid justifyContent="left" item xs zeroMinWidth>
        <h4>{author}</h4>
        <p className={useStyles.content}>{content}</p>
      </Grid>
    </Grid>
    <Divider
      variant="fullWidth"
      className="divider"
      className={useStyles.divider}
    />
  </div>
);

//make CommentForm a functional component
const CommentForm = ()=>{
  const handleSubmit = (event) => {
    event.preventDefault();
    let author = this._author;
    let content = this._content;
    addComment(author.value, content.value);
  }

  return (
    <Grid container spacing={2}>
      <FormControl color="primary" fullWidth="true" className="ml-2 mr-2">
        <TextField id="outlined-basic" label="Name" variant="outlined" className="mb-2" inputRef={(input) => (this._author = input)} />
        <TextField id="outlined-basic" label="Add a comment" variant="outlined" multiline rowsMax={6} rows={4} inputRef={(textField) => (this._content = textField)} />
        <FormHelperText id="my-helper-text"> We'll never share your personal information. </FormHelperText>
        <Button variant="contained" color="primary" type="submit" onClick={handleSubmit}> SUBMIT</Button>
      </FormControl>
    </Grid>
  );
}

