export default class CommentBox extends React.Component {
  constructor() {
    super();

    this.state = {
      comments: [
        {
          id: 1,
          author: "Fiona",
          content:
            "A review is an evaluation of a publication, service, or company such as a movie, video game, musical composition, book; a piece of hardware like a car, home appliance, or computer; or an event or performance, such as a live music concert, play, musical theater show, dance show, or art exhibition.",
        },
        {
          id: 2,
          author: "Dan",
          content:
            "A review is an evaluation of a publication, service, or company such as a movie, video game, musical composition, book; a piece of hardware like a car, home appliance, or computer; or an event or performance, such as a live music concert, play, musical theater show, dance show, or art exhibition.",
        },
      ],
    };
  }

  getComments() {
    return this.state.comments.map((comment) => {
      return (
        <Comment
          key={comment.id}
          author={comment.author}
          content={comment.content}
        />
      );
    });
  }

  addComment(author, content) {
    const comment = {
      id: this.state.comments.length + 1,
      author: author,
      content: content,
    };
    this.setState({
      comments: this.state.comments.concat([comment]),
    });
  }

  render() {
    const commentListStyle = {
      div: {
        padding: "14px",
      },
      paper: {
        padding: "40px 20px",
      },
    };
    return (
      <div style={commentListStyle.div} className="App">
        <h1>Comments</h1>
        <CommentForm addComment={this.addComment.bind(this)} />
        <Paper style={commentListStyle.paper}>{this.getComments()}</Paper>
      </div>
    );
  }
}
