import React from 'react';

class CommentForm extends React.Component{

    render(){
        return (
          <div>
            <form

            >
              <div 
              >
                <input
                  placeholder="Name"
                  required
                  ref={(input) => (this._author = input)}
                ></input>
                <br />
                <textarea
                  placeholder="Comment"
                  rows="4"
                  required
                  ref={(textarea) => (this._body = textarea)}
                ></textarea>
              </div>
              <div 
              >
                <button type="submit">Post Comment</button>
              </div>
            </form>
          </div>
        );
    }

}

export default CommentForm;
