import React from 'react';
import { Grid,Input, Avatar, Divider, Paper} from '@material-ui/core';
import './CommentBox.sass';

// class CommentBox extends React.Component{




// }
class CommentList extends React.Comment{
    constructor(props) {
        super(props);

        this.state = {
            comments: [
                { id: 1, author: 'Fiona', content: 'test1'},
                { id: 2, author: 'Dan', content: 'test2' },
            ]
        }

    }
    render() {
        return (
            <div style={{ padding: 14 }} className="App">
                <h1>Comments</h1>
                <Paper style={{ padding: "40px 20px" }}>
                    <Grid container wrap="nowrap" spacing={2}>
                        {/* {this.props.comments.map(comment)=>{

                        }} */}
                        <Comment name={this.state.comments[0].name} content={this.state.comments[0].conent} key={this.state.comments[0].id}/>
                    </Grid>
                </Paper>
            </div>
        )
    }

}

class Comment extends React.Component{
    render() {
        return (

                    <div>
                        <Grid item>
                            <Avatar alt="Remy Sharp" src='' />
                        </Grid>
                        <Grid justifyContent="left" item xs zeroMinWidth>
                            <h2 style={{ margin: 0, textAlign: "left" }}>{this.props.name}</h2>
                            <p style={{ textAlign: "left" }}>{this.props.content}</p>
                            <p style={{ textAlign: "left", color: "gray" }}>
                                posted 1 minute ago
                            </p>
                        </Grid>

                    <Divider variant="fullWidth" style={{ margin: "30px 0" }} />
            </div>
        )
    }
}
export default CommentList;
