import React from 'react';
import Comment from '../Comment/Comment';
import {
    FormControl,
    TextField,
    FormHelperText,
    Button,
    Grid,
    Paper
} from "@material-ui/core";



class CommentForm extends React.Component{

    constructor(props){
        super(props);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onSubmit(event){
        event.preventDefault();
        let author = this.refs.author;
        let content = this.refs.content;
        this.props.addComment(author.value,content.value);
    }

    render(){
        return (
          <Grid container spacing={2}>
            <FormControl color="primary" fullWidth="true" className="ml-2 mr-2">
              <input
                id="outlined-basic"
                label="Name"
                variant="outlined"
                className="mb-2"
                ref="author"
              />
              <input
                id="outlined-basic"
                label="Add a comment"
                variant="outlined"
                multiline
                rowsMax={6}
                rows={4}
                ref="content"
              />
              <FormHelperText id="my-helper-text">
                We'll never share your personal information.
              </FormHelperText>
              <Button variant="contained" color="primary" type="submit">
                SUBMIT
              </Button>
            </FormControl>
          </Grid>
        );
    }
}

export default class CommentBox extends React.Component{
    constructor(){
        super();

        this.state ={
            comments:[
                 {
                     id: 1,
                     author: "Fiona",
                     content: "A review is an evaluation of a publication, service, or company such as a movie, video game, musical composition, book; a piece of hardware like a car, home appliance, or computer; or an event or performance, such as a live music concert, play, musical theater show, dance show, or art exhibition.",
                 }, {
                     id: 2,
                     author: "Dan",
                     content: "A review is an evaluation of a publication, service, or company such as a movie, video game, musical composition, book; a piece of hardware like a car, home appliance, or computer; or an event or performance, such as a live music concert, play, musical theater show, dance show, or art exhibition.",
                 }
            ]
        };
    }

    getComments(){
        return this.state.comments.map((comment)=>{
            return <Comment id={comment.id} author={comment.author} content={comment.content} />
            });
    }

    addComment(author, content){
        const comment={
            id: this.state.comments.length+1,
            author,
            content
        };
        const newCommentList = this.state.comments.push(comment);
        this.setState({ comments : newCommentList});
    }

   render() {
        const commentListStyle = {
          div: {
            padding: "14px",
          },
          paper: {
            padding: "40px 20px"
          },
        };
        return (
            <div style={commentListStyle.div} className="App">
            <h1>Comments</h1>
            <CommentForm />
            <Paper style={commentListStyle.paper}>
              {this.getComments()}
            </Paper>
          </div>
        );
    }
}
